import Box from './component/Box';
import 유저주먹 from "./assets/유저-주먹.jpg";
import 유저보 from "./assets/유저-보.jpg";
import 유저가위 from "./assets/유저-가위.jpg";
import './App.css';
import { useState } from 'react';

const choice = {
  주먹: {
    name: "주먹",
    img: 유저주먹
  },
  보: {
    name: "보",
    img: 유저보
  },
  가위: {
    name: "가위",
    img: 유저가위
  }
};

// 1. 박스 두 개 만들기. (플레이어, 사진, 승패 결과)
// 2. 유저는 박스 하단의 버튼을 볼 수 있다.
// 3. 유저가 버튼을 클릭하면 클릭한 아이템이 유저 박스에 보인다.
// 4. 유저가 버튼을 클릭하면 컴퓨터 아이템은 랜덤으로 선택된다.
// 5. 3, 4번 아이템을 통해 승패 결과를 보여준다.
// 6. 결과에 따라 박스 테두리의 색이 변한다. (이김: 초록, 짐: 빨강, 비김: 검정)
function App(props) {
  const [userImage, setClickPlay] = useState(0);
  const play = (userChoice) => {
    setClickPlay(choice[userChoice]);
  };
  return (
    <div>
      <div className="box-cover">
        <Box name="유저" image={userImage}/>
        <Box name="컴퓨터" image={userImage}/>
      </div>
      <div className="box-cover">
        <button onClick={() => play("주먹")}>주먹</button>
        <button onClick={() => play("보")}>보</button>
        <button onClick={() => play("가위")}>가위</button>
      </div>
    </div>
  );
}

export default App;
