import Box from './component/Box';
import 유저주먹 from "./assets/유저-주먹.jpg";
import 유저보 from "./assets/유저-보.jpg";
import 유저가위 from "./assets/유저-가위.jpg";
import 컴퓨터주먹 from "./assets/컴퓨터-주먹.jpg";
import 컴퓨터보 from "./assets/컴퓨터-보.jpg";
import 컴퓨터가위 from "./assets/컴퓨터-가위.jpg";
import './App.css';
import { useState } from 'react';

const choice = {
  주먹: {
    name: "주먹",
    img: 유저주먹,
    num: "0"
  },
  보: {
    name: "보",
    img: 유저보,
    num: "1"
  },
  가위: {
    name: "가위",
    img: 유저가위,
    num: "2"
  }
};

// 1. 박스 두 개 만들기. (플레이어, 사진, 승패 결과)
// 2. 유저는 박스 하단의 버튼을 볼 수 있다.
// 3. 유저가 버튼을 클릭하면 클릭한 아이템이 유저 박스에 보인다.
// 4. 유저가 버튼을 클릭하면 컴퓨터 아이템은 랜덤으로 선택된다.
// 5. 3, 4번 아이템을 통해 승패 결과를 보여준다.
// 6. 결과에 따라 박스 테두리의 색이 변한다. (이김: 초록, 짐: 빨강, 비김: 검정)
function App(props) {
  const [userImage, setClickPlay] = useState(0);
  const [computerImage, setAutoPlay] = useState(0);
  const [userResult, setUserResult] = useState("");
  const [computerResult, setComResult] = useState("");
  const play = (userChoice) => {
    setClickPlay(choice[userChoice]);
      let randomNum = Math.floor((Math.random()*10)%3);
      // console.log(randomNum);
      if (randomNum == 0) {
        setAutoPlay(컴퓨터주먹);
      } else if (randomNum == 1) {
        setAutoPlay(컴퓨터보);
      } else {
        setAutoPlay(컴퓨터가위); // 2
      }
      judgement(choice[userChoice], randomNum);
    };

    // 0 1 2 
    //        이김  짐  비김
    // 주먹0 - 가위2 보1  주먹0
    // 보1 - 주먹0  가위2 보1
    // 가위2 - 보1  주먹0 가위2

    const judgement = (user,computer) => {
      // console.log(user, computer);
      if (user.num == computer){ setUserResult("비김")}
      console.log(user.num);
      console.log(computer);
      (user.num == 0 && computer == 2) ? setUserResult("이김") : setUserResult("짐");
      (user.num == 1 && computer == 0) ? setUserResult("이김") : user.num == computer ? setUserResult("비김") : setUserResult("짐");
      (user.num == 2 && computer == 1) ? setUserResult("이김") : user.num == computer ? setUserResult("비김") : setUserResult("짐");
    }
  return (
    <div>
      <h1 className="box-cover">가위바위보 게임 하자.</h1>
      <div className="box-cover">
        <Box name="유저" image={userImage} result={userResult}/>
        <Box name="컴퓨터" image1={computerImage} result={computerResult}/>
      </div>
      <div className="box-cover">
        <button onClick={() => play("주먹")}>주먹</button>
        <button onClick={() => play("보")}>보</button>
        <button onClick={() => play("가위")}>가위</button>
      </div>
    </div>
  );
}

export default App;
