import './App.css';

// 1. 박스 두 개 만들기. (플레이어, 사진, 승패 결과)
// 2. 유저는 박스 하단의 버튼을 볼 수 있다.
// 3. 유저가 버튼을 클릭하면 클릭한 아이템이 유저 박스에 보인다.
// 4. 유저가 버튼을 클릭하면 컴퓨터 아이템은 랜덤으로 선택된다.
// 5. 3, 4번 아이템을 통해 승패 결과를 보여준다.
// 6. 결과에 따라 박스 테두리의 색이 변한다. (이김: 초록, 짐: 빨강, 비김: 검정)
function App() {
  return (
    <div>
      <section>
        <div class="box">
          <div>유저</div>
          <img src=""/>
          <div>승패 결과</div>
        </div>
        <div class="box">
          <div>컴퓨터</div>
          <img src=""/>
          <div>승패 결과</div>
        </div>
      </section>
      <section>
        <button>가위</button>
        <button>바위</button>
        <button>보</button>
      </section>
    </div>
  );
}

export default App;
